import { useState } from "react";

export default function sortButton({title, onSort, onReset}) {
  const [sort, setSort] = useState(undefined);
  const handleClick = () => {
    switch (sort) {
      case false:
        setSort(true);
        onSort(title, false);
        break;
      case true:
        setSort(undefined);
        onReset();
        break;
      case undefined:
        setSort(false);
        onSort(title, true);
        break;
    }
  };
return <button onClick={handleClick}>{title}:{String(sort)}</button>;
}
