import Line from "../components/Row";
import fetch from "isomorphic-unfetch";
import { useState, useMemo } from "react";

import SortId from "../components/sort";
import Table from "../components/table";
import SortButton from "../components/sortButton";

const styles = {
  root: {
    margin: 0,
    width: "100%"
  }
};

export default function Index({ humans }) {
  const [items, setItems] = useState(humans);
  const headers = useMemo(() => {
    return Object.keys(humans[0]);
  }, [humans]);

  const handleReset = () => {
    setItems(humans);
  };

  const handleSort = (name, reverse) => {
    console.log(name)
    if (reverse) {
      setItems(
        items.sort((a, b) => {
          return b[name] - a[name];
        })
      );
    } else {
      setItems(
        items.sort((a, b) => {
          return a[name] - b[name];
        })
      );
    }
  };
  const renderHeader = () => {
    return (
      <thead>
        <tr>
          {headers.map(header => {
            return (
              <th key={header}>
                <SortButton
                  title={header}
                  onReset={handleReset}
                  onSort={handleSort}
                />
              </th>
            );
          })}
        </tr>
      </thead>
    );
  };
  return (
    <div style={styles.root}>
      <Table humans={humans} renderHeader={renderHeader} />
    </div>
  );
}

Index.getInitialProps = async function() {
  const res = await fetch(
    "http://www.filltext.com/?rows=32&id=%7Bnumber%7C1000%7D&firstName=%7BfirstName%7D&lastName=%7BlastName%7D&email=%7Bemail%7D&phone=%7Bphone%7C(xxx)xxx-xx-xx%7D&address=%7BaddressObject%7D&description=%7Blorem%7C32%7D"
  );
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    humans: data
  };
};
