import Line from "../components/line"
import fetch from "isomorphic-unfetch";
import { useState } from "react";

import SortId from "../components/sort";

const styles = {
  root: {
    margin:0,
    width:"100%"
  },}
  function sortId(humans) {
    humans.sort(function(a, b) {
      return a.id - b.id;
    });
  }
  function rsortId(humans) {

       return humans.reverse();

  }

export default function Index(props) {
  const [sort, setSort] = useState(false);
  const toDoOpen = () => {
    setSort(!sort);
    if (sort){
    sortId(humans)}
    if (!sort) {
      rsortId(humans)}
  };
  let oneLine = {"id":"id", "firstName":"firstName", "lastName":"lastName", "email":"email", "phone":"phone"};
  let humans = props.humans;
    return (
      <div style={styles.root}>
        <Line human={oneLine} />
        {/* <SortId humans={humans}/> */}
        {humans.map(human => (
          <Line human={human} />

        ))}
        <button  onClick={toDoOpen}>Сортировка</button>
      </div>
    );
  };

  Index.getInitialProps = async function() {
    const res = await fetch('http://www.filltext.com/?rows=32&id=%7Bnumber%7C1000%7D&firstName=%7BfirstName%7D&lastName=%7BlastName%7D&email=%7Bemail%7D&phone=%7Bphone%7C(xxx)xxx-xx-xx%7D&address=%7BaddressObject%7D&description=%7Blorem%7C32%7D');
    const data = await res.json();

    console.log(`Show data fetched. Count: ${data.length}`);

    return {
      humans: data
    };
  };
