import { useState } from "react";

export default function SortButton(title, onSort, onReset) {
  const [sort, setSort] = useState(undefined);
  const handleClick = () => {
    switch (sort) {
      case false:
        setSort(true);
        onSort(title, true);
        break;
      case true:
        setSort(undefined);
        onReset();
        break;
      case undefined:
        setSort(false);
        onSort(title, false);
        break;
    }
  };
  console.log(title)
  return <button onClick={handleClick}>{title}</button>;
}
