import Row from "./Row";
import {useMemo} from "react";

const styles = {
  pagesNumber: {
    listStyle: "none",
    display: "flex",
    color: "blue",
    userSelect: "none",
    cursor: "pointer",
  }
};
export default function Table({ items, renderHeader, renderPageNumbers, onClickRow, loading }) {


  return (
    <>
    <table>
      {loading? (<p></p>):(
      renderHeader())}
      <tbody>
        {items.map(item => (
          <Row human={item} key={`${item.id}:${item.firstName}-${item.lastName}`} onClickRow={onClickRow}/>
        ))}
      </tbody>
    </table>
    <ul style={styles.pagesNumber}>
    {renderPageNumbers}
            </ul>
</>
  );
}
