import {useState} from "react"



export default function Add(){

    const [addItem, setAddItem]= useState(false)
    const [newItem, setNewItem]= useState()
    const [id, setId]= useState()
    const [firstName, setFirstName]= useState()
    const [lastName, setLastName]= useState()
    const [email, setEmail]= useState()
    const [phone, setPhone]= useState()


    const handleClick = () => {
setAddItem(true)
    }

    const handleChangeID=(event) =>{
        setId(event.target.value)   
    }
    const handleChangeFirstName=(event) =>{
        setFirstName(event.target.value)   
    }
    const handleChangeLastName=(event) =>{
        setLastName(event.target.value)   
    }
    const handleChangeEmail=(event) =>{
        setEmail(event.target.value)   
    }
    const handleChangePhone=(event) =>{
        setPhone(event.target.value)   
    }
    const handleClickItem = () => {
        setNewItem({"id":id,"firstName":firstName,"lastName":lastName,"email":email,"phone":phone})
            }

    return(
        <div>
        <button onClick={handleClick}>Добавить</button>

        { addItem && <div>
        id
        <input type="text" name="id" onChange={handleChangeID} />

        firstName
        <input type="text" name="firstName" onChange={handleChangeFirstName}   />
        lastName
        <input type="text" name="lastName" onChange={handleChangeLastName}  />
        email
        <input type="text" name="email" onChange={handleChangeEmail} />
        phone
        <input type="text" name="phone" onChange={handleChangePhone} />

        </div>}
        {id && firstName && lastName && email && phone && <button onClick={handleClickItem}> Добавить в таблицу </button> }
        </div>
    )
}
