import fetch from "isomorphic-unfetch";
import { useState, useMemo, useEffect } from "react";
import Row from "../components/Row"
import Table from "../components/table";
import SortButton from "../components/sortButton";

const styles = {
  root: {
    margin: 0,
    width: "100%"
  }
};

export default function Index({ humans }) {
  const [items, setItems] = useState(humans);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);


  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;

  const [currentItems, setCurrentItemss] = useState(items.slice(indexOfFirstItem, indexOfLastItem));
  // const renderItems = currentItems.map((item) => {
  //   return <Row key={item.id} human={item}/>;
  // });


  const handleClick = (event) => {

      setCurrentPage(Number(event.target.id)) 
    }

    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(items.length / itemsPerPage); i++) {
      pageNumbers.push(i);
    }

    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <li
          key={number}
          id={number}
          onClick={handleClick}
        >
          {number}
        </li>
      );
    });



  const headers = useMemo(() => {
    return Object.keys(humans[0]);
  }, [humans]);

  useEffect(()=> {
    setItems(humans);
  }, [humans]);

  const handleReset = () => {
    setItems(humans);
  };

  const handleSort = (name, reverse) => {
    const newItems = [...items];
    if (reverse) {
      newItems.sort((a, b) => {
        if(typeof a[name] === 'string') {
          return a[name].localeCompare(b[name]);
        } else if(typeof a[name] === 'number') {
          return +a[name] > +b[name];
        }
      });
    } else {
      newItems.sort((a, b) => {
        if(typeof a[name] === 'string') {
          return b[name].localeCompare(a[name]);
        } else if(typeof a[name] === 'number') {
          return +a[name] < +b[name];
        }
      });
    }
    setItems(newItems);
    setCurrentItems(items.slice(indexOfFirstItem, indexOfLastItem));
  };
  const renderHeader = () => {
    return (
      <thead>
        <tr>
          {headers.map(header => {
            return (
              <th key={header}>
                <SortButton
                  title={header}
                  onReset={handleReset}
                  onSort={handleSort}
                />
              </th>
            );
          })}
        </tr>
      </thead>
    );
  };
  return (
    <div style={styles.root}>
      {/* {renderItems} */}
      <Table items={currentItems} renderHeader={renderHeader} renderPageNumbers={renderPageNumbers} />
    </div>
  );
}

Index.getInitialProps = async function() {
  const res = await fetch(
    "http://www.filltext.com/?rows=1000&id=%7Bnumber%7C1000%7D&firstName=%7BfirstName%7D&delay=3&lastName=%7BlastName%7D&email=%7Bemail%7D&phone=%7Bphone%7C(xxx)xxx-xx-xx%7D&address=%7BaddressObject%7D&description=%7Blorem%7C32%7D");
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    humans: data
  };
};
