import fetch from "isomorphic-unfetch";
import { useState, useMemo, useEffect } from "react";
import Table from "../components/table";
import SortButton from "../components/sortButton";
import Description from "../components/description";

const styles = {
  root: {
    margin: 0,
    width: "100%"
  }
};

export default function Index({ humans }) {
  const [items, setItems] = useState(humans);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(50);
  const [selectedItem, setSelectedItem] = useState();
  const [loading, setLoading] = useState(false);
  const componentDidMount = () => {
    fetch("https://swapi.co/api/people/1/?format=json")
      .then(response => response.json())
      .then(data => setItems(data));
  };
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = items.slice(indexOfFirstItem, indexOfLastItem);

  const handleClick = event => {
    setCurrentPage(Number(event.target.id));
    setSelectedItem();
  };

  const handleClickRow = e => {
    setSelectedItem(e);
  };

  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(items.length / itemsPerPage); i++) {
    pageNumbers.push(i);
  }

  const renderPageNumbers = pageNumbers.map(number => {
    return (
      <li key={number} id={number} onClick={handleClick}>
        {number}
      </li>
    );
  });

  const headers = useMemo(() => {
    return Object.keys(humans[0]);
  }, [humans]);

  useEffect(() => {
    setItems(humans);
  }, [humans]);

  useEffect(() => {
    fetch("http://www.filltext.com/?rows=1000&id=%7Bnumber%7C1000%7D&firstName=%7BfirstName%7D&delay=3&lastName=%7BlastName%7D&email=%7Bemail%7D&phone=%7Bphone%7C(xxx)xxx-xx-xx%7D&address=%7BaddressObject%7D&description=%7Blorem%7C32%7D")
      .then(response => response.json())
      .then(data => alert(data));
  }, []);

  const handleReset = () => {
    setItems(humans);
  };

  const handleSort = (name, reverse) => {
    const newItems = [...items];
    if (reverse) {
      newItems.sort((a, b) => {
        if (typeof a[name] === "string") {
          return a[name].localeCompare(b[name]);
        } else if (typeof a[name] === "number") {
          return +a[name] > +b[name];
        }
      });
    } else {
      newItems.sort((a, b) => {
        if (typeof a[name] === "string") {
          return b[name].localeCompare(a[name]);
        } else if (typeof a[name] === "number") {
          return +a[name] < +b[name];
        }
      });
    }
    setItems(newItems);
    setSelectedItem();
  };
  const renderHeader = () => {
    return (
      <thead>
        <tr>
          {headers.map(header => {
            return (
              <th key={header}>
                <SortButton
                  title={header}
                  onReset={handleReset}
                  onSort={handleSort}
                />
              </th>
            );
          })}
        </tr>
      </thead>
    );
  };

  const renderDescription = () => {
    if (selectedItem > 0) {
      return (
        <Description
          item={
            currentItems[
              currentItems.findIndex(item => item.id == selectedItem)
            ]
          }
        />
      );
    }
  };

  return (
    <div style={styles.root}>
      <Table
        items={currentItems}
        renderHeader={renderHeader}
        renderPageNumbers={renderPageNumbers}
        onClickRow={handleClickRow}
      />
      {renderDescription()}
    </div>
  );
}

