import fetch from "isomorphic-unfetch";
import { useState, useMemo, useEffect } from "react";
import Table from "../components/table";
import SortButton from "../components/sortButton";
import Description from "../components/description";
import Add from "../components/add";

const styles = {
  root: {
    margin: 0,
    width: "100%"
  }
};

export default function Index({ humans }) {
  const [resItems, setResItems] = useState();
  const [items, setItems] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(50);
  const [selectedItem, setSelectedItem] = useState();
  const [loading, setLoading] = useState(false);



  let currentItems = 0;
  const fcurrentPage=()=>{
    let indexOfLastItem = currentPage * itemsPerPage;
    let indexOfFirstItem = indexOfLastItem - itemsPerPage;
    currentItems = items.slice(indexOfFirstItem, indexOfLastItem);}

  const handleClick = event => {
    setCurrentPage(Number(event.target.id));
    setSelectedItem();
  };

  const handleClickRow = e => {
    setSelectedItem(e);
  };

  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(items.length / itemsPerPage); i++) {
    pageNumbers.push(i);
  }

  const renderPageNumbers = pageNumbers.map(number => {
    return (
      <li key={number} id={number} onClick={handleClick}>
        {number}
      </li>
    );
  });

  const headers = useMemo(() => {
    return Object.keys(humans[0]);
  }, [humans]);

  useEffect(() => {
    setItems(humans);
  }, [humans]);

  useMemo(() => {
    fcurrentPage()
  }, [items]);


   useEffect(() => {
     setLoading(true);
     fetch("http://www.filltext.com/?rows=32&id={number|1000}&firstName={firstName}&lastName={lastName}&email={email}&phone={phone|(xxx)xxx-xx-xx}&address={addressObject}&description={lorem|32}")
          .then(response => response.json())
          .then(data => setResItems(data))
    },[]);

  const handleReset = () => {
    setItems(humans);
  };

  const handleSort = (name, reverse) => {
    const newItems = [...items];
    if (reverse) {
      newItems.sort((a, b) => {
        if (typeof a[name] === "string") {
          return a[name].localeCompare(b[name]);
        } else if (typeof a[name] === "number") {
          return +a[name] > +b[name];
        }
      });
    } else {
      newItems.sort((a, b) => {
        if (typeof a[name] === "string") {
          return b[name].localeCompare(a[name]);
        } else if (typeof a[name] === "number") {
          return +a[name] < +b[name];
        }
      });
    }
    setItems(newItems);
    setSelectedItem();
  };

  const addNewItem = newItem => {
    setItems(items.push(newItem));
    console.log(items);
  };
  const renderHeader = () => {
    return (
      <thead>
        <tr>
          {headers.map(header => {
            return (
              <th key={header}>
                <SortButton
                  title={header}
                  onReset={handleReset}
                  onSort={handleSort}
                />
              </th>
            );
          })}
        </tr>
      </thead>
    );
  };

  const renderDescription = () => {
    if (selectedItem > 0) {
      return (
        <Description
          item={
            currentItems[
              currentItems.findIndex(item => item.id == selectedItem)
            ]
          }
        />
      );
    }
  };

  return (
    <div style={styles.root}>
      <Add addNewItem={addNewItem} />
      <Table
        items={currentItems}
        renderHeader={renderHeader}
        renderPageNumbers={renderPageNumbers}
        onClickRow={handleClickRow}
      />
      {renderDescription()}
    </div>
  );
}

Index.getInitialProps = async function() {
  const res = await fetch(
    "http://www.filltext.com/?rows=32&id={number|1000}&firstName={firstName}&lastName={lastName}&email={email}&phone={phone|(xxx)xxx-xx-xx}&address={addressObject}&description={lorem|32}"
  );
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    humans: data
  };
};
